using UnityEngine;

public class UIHelper : MonoBehaviour
{
    public Color gizmoColor = Color.yellow;

    private void OnDrawGizmos()
    {
        // Draw a gizmo to help visualize the UI element in scene view
        RectTransform rectTransform = GetComponent<RectTransform>();
        if (rectTransform != null)
        {
            Gizmos.color = gizmoColor;
            Vector3 center = rectTransform.position;
            Vector3 size = rectTransform.rect.size;

            // Scale size based on local scale
            size.x *= rectTransform.lossyScale.x;
            size.y *= rectTransform.lossyScale.y;

            Gizmos.DrawWireCube(center, size);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using TMPro;

public class StockManager : MonoBehaviour
{
    [System.Serializable]
    public class StockItem
    {
        public string name;
        public int quantity;
        public float price;
    }

    public TMP_InputField itemNameInput;
    public TMP_InputField quantityInput;
    public TMP_InputField priceInput;
    public Button addButton;
    public Transform itemListContainer;
    public GameObject itemPrefab;

    private List<StockItem> stockItems = new List<StockItem>();

    private void Start()
    {
        if (addButton != null)
        {
            addButton.onClick.AddListener(AddItem);
        }
        else
        {
            Debug.LogError("Add button not assigned in StockManager");
        }
    }

    public void AddItem()
    {
        if (string.IsNullOrEmpty(itemNameInput.text))
        {
            Debug.LogWarning("Item name cannot be empty");
            return;
        }

        int quantity = 0;
        float price = 0f;

        if (quantityInput != null)
        {
            int.TryParse(quantityInput.text, out quantity);
        }

        if (priceInput != null)
        {
            float.TryParse(priceInput.text, out price);
        }

        StockItem newItem = new StockItem
        {
            name = itemNameInput.text,
            quantity = quantity,
            price = price
        };

        stockItems.Add(newItem);

        // Clear input fields
        itemNameInput.text = "";
        if (quantityInput != null) quantityInput.text = "";
        if (priceInput != null) priceInput.text = "";

        // Update UI
        UpdateStockList();

        Debug.Log($"Added item: {newItem.name}, Quantity: {newItem.quantity}, Price: {newItem.price}");
    }

    private void UpdateStockList()
    {
        if (itemListContainer == null || itemPrefab == null)
        {
            Debug.LogWarning("Item list container or item prefab not assigned");
            return;
        }

        // Clear existing items
        foreach (Transform child in itemListContainer)
        {
            Destroy(child.gameObject);
        }

        // Add items to the list
        foreach (StockItem item in stockItems)
        {
            GameObject itemGO = Instantiate(itemPrefab, itemListContainer);
            TMP_Text itemText = itemGO.GetComponentInChildren<TMP_Text>();
            if (itemText != null)
            {
                itemText.text = $"{item.name} - Qty: {item.quantity} - Price: ${item.price:F2}";
            }
        }
    }
}
